import{_ as e,c as a,o as i,ae as o}from"./chunks/framework.D2jU6acO.js";const h=JSON.parse('{"title":"Features & Usage","description":"","frontmatter":{},"headers":[],"relativePath":"guide/features.md","filePath":"guide/features.md","lastUpdated":1768647307000}'),r={name:"guide/features.md"};function l(s,t,n,d,u,c){return i(),a("div",null,[...t[0]||(t[0]=[o('<h1 id="features-usage" tabindex="-1">Features &amp; Usage <a class="header-anchor" href="#features-usage" aria-label="Permalink to &quot;Features &amp; Usage&quot;">​</a></h1><h2 id="inline-editing" tabindex="-1">Inline Editing <a class="header-anchor" href="#inline-editing" aria-label="Permalink to &quot;Inline Editing&quot;">​</a></h2><p>The core feature of the application is the spreadsheet-like table. You can edit most fields directly:</p><ul><li><strong>Name</strong>: Click to edit the product title.</li><li><strong>SKU</strong>: Update Stock Keeping Units.</li><li><strong>Price</strong>: Edit Regular and Sale prices.</li><li><strong>Stock</strong>: Update stock quantity quickly.</li></ul><p>Changes are <strong>auto-saved</strong> when you click outside the cell (on blur).</p><h2 id="rich-text-editor" tabindex="-1">Rich Text Editor <a class="header-anchor" href="#rich-text-editor" aria-label="Permalink to &quot;Rich Text Editor&quot;">​</a></h2><p>For <strong>Short Description</strong> and <strong>Main Description</strong>, we provide a full-featured text editor.</p><ol><li>Click the <strong>Edit</strong> button in the <em>Content</em> column.</li><li>A modal window opens with two editors.</li><li><strong>Visual Mode</strong>: Write with bold, italics, lists, and links.</li><li><strong>HTML Mode</strong>: Click &quot;Edit HTML&quot; to view and edit the raw HTML source code. Our smart formatter keeps your code readable.</li></ol><h2 id="bulk-actions" tabindex="-1">Bulk Actions <a class="header-anchor" href="#bulk-actions" aria-label="Permalink to &quot;Bulk Actions&quot;">​</a></h2><p>Apply changes to multiple products at once:</p><ol><li><strong>Select</strong> products using the checkboxes on the left.</li><li>A <strong>Bulk Toolbar</strong> appears at the top.</li><li>Choose an action: <ul><li><strong>Set Status</strong>: Change to Publish, Draft, Private, or Pending.</li><li><strong>Set Stock Status</strong>: Mark as In Stock or Out of Stock.</li></ul></li><li>Click <strong>Apply</strong>.</li></ol><h2 id="custom-meta-fields" tabindex="-1">Custom Meta Fields <a class="header-anchor" href="#custom-meta-fields" aria-label="Permalink to &quot;Custom Meta Fields&quot;">​</a></h2><p>Manage custom data for your products:</p><ol><li>Click the <strong>Settings (Gear)</strong> icon on a product row.</li><li>Add new Key/Value pairs.</li><li>Update or Delete existing meta fields.</li><li>Click <strong>Save</strong>.</li></ol><h2 id="image-management" tabindex="-1">Image Management <a class="header-anchor" href="#image-management" aria-label="Permalink to &quot;Image Management&quot;">​</a></h2><p>Manage your product images directly from the spreadsheet:</p><ul><li><strong>Visual Gallery</strong>: View all your WordPress media in a modal.</li><li><strong>Upload</strong>: Drag and drop support to upload new images.</li><li><strong>Selection</strong>: Easily select and assign images to products.</li></ul><h2 id="add-new-products" tabindex="-1">Add New Products <a class="header-anchor" href="#add-new-products" aria-label="Permalink to &quot;Add New Products&quot;">​</a></h2><p>Quickly create new products without leaving the bulk editor:</p><ul><li><strong>Quick Create</strong>: Click &quot;Add Product&quot; in the toolbar.</li><li><strong>Essential Fields</strong>: Enter Name, Type (Simple/Variable), Price, and SKU.</li><li><strong>Immediate Editing</strong>: The new product appears instantly in the table for further editing.</li></ul>',20)])])}const p=e(r,[["render",l]]);export{h as __pageData,p as default};
